#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define PI     3.1415

void mainImage(out vec4 o, vec2 u) {
    vec3 R = iResolution, q, D;

    float d, s, ss, f, i, j, 
          e = 1e-2, t = iTime;

    u = (u - R.xy * .5) / R.y;
    D = vec3(u, 1.5);
    o *= 0.;

    while(i++ < 44.) {
        q = d * D;
        q.z -= cos(t),
        q.yz *= rot(cos(t * .7) * .2);
        q.zx *= rot(cos(t * .2) * 1.3);

        ss = abs(q.y) - .1;

        j = 4.;
        while(j < 128.) 
            q.xz *= j * .2,
            s = dot(cos(q.xz - t), vec2(1)) / j,
            ss += abs(s * 1.8) - .5,
            j += j;

        ss = abs(ss) * .5 - .01,

        f = 1. - ss / e, 
        s = f / d * i / 400.,

        f > 0.
            ? o += s, s
            : o.b += .005;

        d += max(ss, e / 5.);
    }
}
