void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    uv *= .5;
    float t = 0. + iTime * .4 + sin(iTime + uv.x * 5.) * .1 + cos(iTime + sin(uv.y * 1.) * 10.) * .15;
    vec2 v = sin(uv * .1 + t * .1);
    uv *= 2.5 + sin(t * .5) * .3;
    float angle = t * .03 ;
    for (int i = 0; i < 100; i++)
    {
        uv = (abs(uv) - 1.3) * 1.1;
        uv *= mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    }

    fragColor = vec4(abs(cos(floor((uv.y * 4.2))* 6.)), abs(cos(uv.x + 10.)), abs(sin(floor(uv.y * 1.0))), 1.0);
}
