vec3 raycast(vec3 ro,vec3 rd,out float distance_,float alpha,out bool ifSmoke){
    float lastd0=0.;
    bool hit00=false;
    vec3 ro0=ro;
    vec3 rd0=rd;
    bool a=false;
    ifSmoke=false;
    float t=0.;
    float Marching_S_2=1.;
    int sgn =DE(ro,rd).s;
    for(int i=0;i<MaxRayStep;i++){
        object o=DE(ro,rd);
        if(o.s!=sgn){
            ro=ro0;rd=rd0;
            Marching_S_2*=0.75;
            continue;
        }
        ro0=ro;rd0=rd;
        float d0=o.d;
        float EPSILON=max(EPSILONS*distance(ro,pos_0),EPSILONS_MIN);
        bool b=a&&int(InsideMaterial(ro,o).y)==Type_Smoke;
        bool c=d0<=EPSILON&&d0<lastd0||Marching_S_2<=EPSILONS_MIN||b;
        if(c||distance(ro,Position_00)>Far){
            ifSmoke=b;hit00=c; break;
        }
        a=true;
        if(distance(ro,Position_00)>Far) break;
        float d_d=d0*Marching_S*Marching_S_2;
        rd=mix(rndS(ro),rd,float(rand(ro)<exp(-d_d*alpha)));
        lastd0=d0;ro+=d_d*rd;t+=d_d;

    }
    distance_=mix2(-1.,t,float(hit00));
    return ro;
}
float GGX_G(float VoN,float a,float g){
    float A=float(VoN>0.)*g*VoN;
    float B=(g-1.)*VoN+sqrt(a*a+(1.-a*a)*VoN*VoN);
    return A/B;
}
float GGX_G2(float VoN,float LoN,float VoH,float NoH,float a,float g){
    float G1=GGX_G(VoN,a,g);float G2=GGX_G(LoN,a,g);
    float G3=G1*G2;
    return G3*VoH/max(VoN*NoH*(G1+G2-G3),1e-4);
}
vec3 GGXNormal(vec3 normal,float roughness,float g,vec2 X,vec2 Y,vec3 pos){
    vec3 randN0;randN0.y=-length(normal.xz);
    if(length(normal.xz)<=0.)
        randN0.xz=vec2(1,0);
    else
        randN0.xz=normalize(normal.xz)*normal.y;
    vec3 randN1=cross(normal,randN0);
    float alpha=mix2(X.x,X.y,rand(pos))*2.*PI;
    float tmp=mix2(Y.x,Y.y,rand(pos));
    float beta=acos(clamp(pow(max(0.,(1.-tmp)/(1.+tmp*(roughness*roughness-1.))),1./g),0.,1.));

    return (cos(beta))*normal+sin(beta)*(cos(alpha)*randN0+sin(alpha)*randN1);
}
