        randN0.xz=vec2(1,0);
    else
        randN0.xz=normalize(normal.xz)*normal.y;
    vec3 randN1=cross(normal,randN0);
    float alpha=rand(pos)*2.*PI;
    float tmp=rand(pos);
    float beta=acos(clamp(pow(max(0.,(1.-tmp)/(1.+tmp*(roughness*roughness-1.))),1./g),0.,1.));

    return (cos(beta))*normal+sin(beta)*(cos(alpha)*randN0+sin(alpha)*randN1);
}
float GGXdf(float theta,float a,float g){
    float a2=a*a;
    float cos2=cos(theta);cos2=pow(cos2,g);
    float a2m1=a2-1.;
    return (1.-cos2)/(1.+a2m1*cos2);
}


//sample direct light
vec4 ObjectLight(vec3 ro,vec3 rd,vec3 n,vec3 center,float sR,int id){
    object thit=hit;
    float d=distance(ro,center);
    float CosAlpha;
    vec3 tnor;
    float tmp=rand(ro);
    float cosD_S;

    if(d<=sR){
        CosAlpha=-1.;
        tnor=reflect(rd,n);
    }else{
        float RDivd=sR/d;
        CosAlpha=1./sqrt(1.+RDivd*RDivd);
        tnor=normalize(center-ro);
    }
    cosD_S=sqrt(0.5*(1.+CosAlpha));
    float D_S=abs(acos(cosD_S));

    float EPSILON=max(EPSILONS*distance(ro,pos_0),EPSILONS_MIN);
    object oA=DE(ro,rd);
    object oB=DE(ro-n*EPSILON*2.,rd);
    vec4 MaterialA=InsideMaterial(ro,oA);vec4 MaterialB=InsideMaterial(ro-n*EPSILON*2.,oB);
    float rs=MaterialB.x/MaterialA.x;
    material mt=Material(ro,n,oA);
    float S=0.;
    vec3 randN=GGXNormal(n,mt.R.x,mt.R.z,ro);
    float k=fresnel(-n,randN,rs);
    vec4 rC=rColor(mt.Cs,max(dot(-rd,randN),0.));
    float p=mixp(rC.w,mt.S.x);

    vec3 A,rnd;
    if(rand(ro)<p||rand(ro)>mt.S.y*(1.-k)){
        A=normalize(tnor-rd);
    }else{
        vec3 n0=refract(rd,n,1./rs);
        if(n0!=vec3(0)){
            ro-=n*EPSILON*2.;
            A=normalize(tnor+rd*rs);
            if(length(A)<0.5) A=vec3(0,1,0);
        }else{
            A=-normalize(tnor+rd);
        }
    }
    float Ny=abs(dot(n,A));
    float Nbeta=abs(acos(Ny));
    vec3 N0;N0.y=-length(n.xz);
    N0.xz=(float(length(n.xz)==0.)*vec2(1,0)+normalize(n.xz))*n.y;
    vec3 N1=cross(n,N0);
    float Nalpha=atan(dot(A,N1),dot(A,N0));

    const float PIdiv2=PI/2.;
    const float divPI=1./PI;
    float Ny2=Ny*Ny;
    float deta=cosD_S*cosD_S-Ny2;
    vec2 X,Y;

    float spec=float(rand(ro)<p);
    vec2 R=mix(mt.R.yw,mt.R.xz,spec);

    //calculate the possiblity of the sample area
    if(deta<0.){
        float a1=abs(acos(cosD_S/Ny));
        float S0=0.5*GGXdf(a1,R.x,R.y);
        float S1=0.5*GGXdf(Nbeta+D_S,R.x,R.y);S=(S0+S1);
        if(rand(ro)<S0/(S0+S1)){
            X=vec2(0,a1);Y=-Nalpha+vec2(-PIdiv2,PIdiv2);
        }else{
            X=vec2(0,Nbeta+D_S);Y=Nalpha+vec2(-PIdiv2,PIdiv2);
        }
    }else{
        float cosF=sqrt(deta/(1.-Ny2));
        float F=acos(cosF);
        S=F*divPI*(GGXdf(min(Nbeta+D_S,PIdiv2),R.x,R.y)-GGXdf(Nbeta-D_S,R.x,R.y));
        X=vec2(Nbeta-D_S,min(Nbeta+D_S,PIdiv2));Y=(Nalpha+vec2(-F,F));
    }
    X.x=GGXdf(X.x,R.x,R.y);X.y=GGXdf(X.y,R.x,R.y);Y=Y*divPI*0.5;
    vec3 n0;
    //random sample ray
    if(CosAlpha==-1.){
        n0=GGXNormal(n,R.x,R.y,ro);S=1.;
    }else{
        n0=GGXNormal(n,R.x,R.y,Y,X,ro);
    }

    if(length(n0)<0.5) n0=n;
    rnd=reflect(rd,n0);

    S=abs(S);

    float t;
    vec4 fog=InsideFog(ro,oA);
    bool ifSmoke;
    vec3 tp=raycast(ro+rnd*ShadowOffset,rnd,t,fog.x,ifSmoke);
    if(t<-0.5){
        hit=thit;
        return vec4(0);
    }
    oA=DE(tp,rd);
    vec3 pos=tp;
    vec3 normal=estimateNormal(pos);
    mt=Material(pos,normal,oA);
    hit=thit;

    vec3 sampleColor=100.*mt.light;
    rC=rColor(mt.Cs,max(dot(-rd,n0),0.));
    sampleColor*=mt.Cd*(1.-mt.S.x*rC.rgb)+mt.S.x*rC.rgb;
    float G1=GGX_G2(dot(-rd,n),dot(rnd,n),dot(rnd,n0),dot(n,n0),R.x,R.y);
    float G2=GGX_G(dot(rnd,n),R.x,R.y);
    sampleColor*=mix2(G2,G1,spec);
    return vec4(sampleColor*clamp(dot(faceforward(n,rd,n),rnd),0.,1.),S);

}

vec4 SunLight(vec3 ro,vec3 rd,vec3 n){
    const float S_R=0.125;
    float cosD_S=1./sqrt(1.+S_R*S_R);cosD_S=sqrt(0.5*(1.+cosD_S));
    float D_S=abs(acos(cosD_S));

    float EPSILON=max(EPSILONS*distance(ro,pos_0),EPSILONS_MIN);
    object oA=DE(ro,rd);
    object oB=DE(ro-n*EPSILON*2.,rd);
    vec4 MaterialA=InsideMaterial(ro,oA);vec4 MaterialB=InsideMaterial(ro-n*EPSILON*2.,oB);
    float rs=MaterialB.x/MaterialA.x;
    material mt=Material(ro,n,oA);

    float S=0.;    
    vec3 randN=GGXNormal(n,mt.R.x,mt.R.z,ro);
    float k=fresnel(-n,randN,rs);
    vec4 rC=rColor(mt.Cs,max(dot(-rd,randN),0.));
    float p=mixp(rC.w,mt.S.x);

    vec3 A,rnd;
    if(rand(ro)<p||rand(ro)>mt.S.y*(1.-k)){
        A=-normalize(lightDir+rd);
    }else{
        vec3 n0=refract(rd,n,1./rs);
        if(n0!=vec3(0)){
            ro-=n*EPSILON*2.;
            A=normalize(lightDir+rd*rs);
            if(length(A)<0.5) A=vec3(0,1,0);
        }else{
            A=-normalize(lightDir+rd);
        }
    }
    float Ny=abs(dot(n,A));
    float Nbeta=abs(acos(Ny));
    vec3 N0;N0.y=-length(n.xz);
    N0.xz=(float(length(n.xz)==0.)*vec2(1,0)+normalize(n.xz))*n.y;
    vec3 N1=cross(n,N0);
    float Nalpha=atan(dot(A,N1),dot(A,N0));


    const float PIdiv2=PI/2.;
    const float divPI=1./PI;
    float Ny2=Ny*Ny;
    float deta=cosD_S*cosD_S-Ny2;
    vec2 X,Y;

    float spec=float(rand(ro)<p);
    vec2 R=mix(mt.R.yw,mt.R.xz,spec);

    if(deta<0.){
        float a1=abs(acos(cosD_S/Ny));
        float S0=0.5*GGXdf(a1,R.x,R.y);
        float S1=0.5*GGXdf(Nbeta+D_S,R.x,R.y);S=(S0+S1);
        if(rand(ro)<S0/(S0+S1)){
            X=vec2(0,a1);Y=-Nalpha+vec2(-PIdiv2,PIdiv2);
        }else{
            X=vec2(0,Nbeta+D_S);Y=Nalpha+vec2(-PIdiv2,PIdiv2);
        }
    }else{
        float cosF=sqrt(deta/(1.-Ny2));
        float F=acos(cosF);
        S=F*divPI*(GGXdf(min(Nbeta+D_S,PIdiv2),R.x,R.y)-GGXdf(Nbeta-D_S,R.x,R.y));
        X=vec2(Nbeta-D_S,min(Nbeta+D_S,PIdiv2));Y=(Nalpha+vec2(-F,F));
    }
    X.x=GGXdf(X.x,R.x,R.y);X.y=GGXdf(X.y,R.x,R.y);Y=Y*divPI*0.5;
    vec3 n0=GGXNormal(n,R.x,R.y,Y,X,ro);
    if(length(n0)<0.5) n0=n;
    rnd=reflect(-rd,n0);
    if(dot(rnd,lightDir)<0.) return vec4(0);
    float t;

    vec4 fog=InsideFog(ro,oA);
    bool ifSmoke;
    vec3 tp=raycast(ro-rnd*ShadowOffset,-rnd,t,fog.x,ifSmoke);
    if(t>-0.5) return vec4(0);
    vec4 tmp=background(ro,-rnd,-lightDir);

    S=abs(S);
    vec3 sampleColor=tmp.xyz*100.;
    rC=rColor(mt.Cs,max(dot(-rd,n0),0.));
    sampleColor*=mt.Cd*(1.-mt.S.x*rC.rgb)+mt.S.x*rC.rgb;

    float G1=GGX_G2(dot(-rd,n),dot(-rnd,n),dot(-rnd,n0),dot(n,n0),R.x,R.y);
    float G2=GGX_G(dot(-rnd,n),R.x,R.y);
    sampleColor*=mix2(G2,G1,spec);
    return max(vec4(0),vec4(sampleColor,S)*(dot(rnd,lightDir)-0.997>0.?1.:0.));
}

vec2 Projection(vec2 p,float S){
    #ifdef SphereProjection
        if(length(p)==0) return p;
        return normalize(p)*tan(length(p)*S);
    #else
        return p*S;
    #endif
}
