float circle (vec2 uv, float radius, float min, float max)
{
    min = radius * min;
    max = radius * max;
    return smoothstep(min,max,length(uv));
}
float saturate (float val)
{
    return clamp(val,0.,1.);
}


vec4 bgEye = vec4(.89,0.89,0.89,1.);
vec4 irisColour = vec4(.1,0.05,0.1,1.);
vec4 corneaColour = vec4(.02,0.01,0.1,1.);
vec4 centreGlow = vec4(1.,0.89,0.89,1.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float scFrac = iResolution.x/iResolution.y;

    vec4 col = vec4(0.,0.,0.,0.);

    vec2 mousePos = iMouse.xy/iResolution.xy - 0.5;
    vec2 uvProp = vec2 ((uv.x-0.5) * scFrac, uv.y-0.5);
    vec2 centredUV = uvProp - 0.5;
    vec2 squintUV = vec2(uvProp.x, uvProp.y / (sin(iTime)*10.));


    float retina = circle(uvProp, 0.2,1.,.99);
    float cornea = circle(uvProp- (mousePos / 5.), 0.1 - (atan(cos(sin(iTime) * 0.8)) / 100.),1.5,.7);
    float iris = circle(uvProp- (mousePos / 4.5), 0.11,1.,.99);
    retina -= 0.1;
    retina -= saturate(iris-0.9);
    retina -= saturate(cornea-0.8);

    float squint = circle(squintUV,0.21,1.,.99);
    float shapeUpper = circle((uvProp - vec2(mousePos.x,(0.5 + (mousePos.y/5.)))),.5,1.,.99);
    retina *=squint;
    retina -= shapeUpper;

    float highlight = circle(((uv - 0.5) - (retina/20.)) - (mousePos / 5.), .02, 1.,0.99);
    if (retina <= cornea - 0.5)
    {
        highlight = 0.;
    }


    if(retina == .9)
    {
        col = bgEye;
    }

    if(retina >= .5)
    {
        col += irisColour;
    }
    if(retina >= .5)
    {
        col += cornea * corneaColour;
        clamp(col,1.,0.);
    }

    if(retina >= .9)
    {
        col += .1;
    }

    //col = vec4(vec3(retina),1.);

    fragColor = col;

}
