#define scale (2.0*sqrt(3.0))

void mainImage(out vec4 o, vec2 I)
{
    vec2 R = iResolution.xy;
    I = (2. * I - R) / R.y; // mind aspect ratio

    mat2x4 T =
        mat2x4
        (
            normalize(vec4(1,0,+1,-1)),
            normalize(vec4(0,1,+1,+1))
        );

    // lift 2d triangular grid coordinates to 4d hyper cubic grid coordinates (offset chooses cutting plane)
    vec4 position = T*I*scale;

    ivec4 tile = ivec4(floor(position));
    bool flip = ((tile.x ^ tile.y ^ tile.z ^ tile.w) & 1) == 0;
    o = vec4(vec3(flip),1);
    o = sqrt(vec4(o)); // gamma
}

