void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // setup pixel counts of horizontal and vertical divisions
    vec2 uv = fragCoord/iResolution.xy;

    // process video
    vec2 resolution = vec2(50., 20.);
    vec2 remapped = round(resolution * uv) / resolution;
    // the above basically collapses our uv coordinates to a discrete field instead of a continuous one
    // by sampling the texture with these new uvs, we find multiple pixels now map to the same uv coord, 
    // giving the effect of pixelation
    vec3 col = texture(iChannel0, remapped).rgb;

    // convert video to grayscale based on average of RGB channels
    col = vec3(col.r + col.g + col.b) / 3.;

    // bump contrast
    col *= col * col * 3. - .2;


    vec2 fontGrid = vec2(16.);

    // 0 - 1
    float sampleInd = fract(uv.y + 3. * abs(fract(10.*remapped.x+iTime)+abs(5.*sin(remapped.x * 3.14) + sin(remapped.x * 10.))));
    sampleInd = round(sampleInd * fontGrid.y) / fontGrid.y;


    // row col offset to start sample into grid
    vec2 fontStart = vec2(6., 1.) / fontGrid;

    vec2 sampleCoord = vec2(uv.x - remapped.x, sampleInd + uv.y - remapped.y) 
                     + vec2(fract(.5 + uv.x * resolution.x), (fract(uv.y * resolution.y)));

    col *= texture(iChannel1, fontStart + sampleCoord/fontGrid).rrr;
    //col = vec3(sampleCoord, 0.);

    // color correct to matrix green
    col *= vec3(.1, 1., .3);

    // Output to screen
    fragColor = vec4(col,1.0);
}
