void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pos = 0.5;
    if (iMouse.xy != vec2(0)) pos = iMouse.x / iResolution.x;

    vec2 pixel = 1.0 / iResolution.xy;
    vec2 uv = fragCoord / iResolution.xy;

    if (uv.x < (pos - pixel.x))
        fragColor = texture(iChannel0, uv);
    else if (uv.x > (pos + pixel.x))
        fragColor = texture(iChannel1, uv);
    else
        fragColor = vec4(1.0, vec3(0));
}

float factor = 0.3;

vec2 diskPt(vec2 p) {
    return sqrt(p.x) * vec2(sin(6.2832 * p.y), cos(6.2832 * p.y));
}

vec2 hash21(float p) {
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec4 prv = texture(iChannel1, uv);
    vec4 cam = texture(iChannel0, uv + (diskPt(hash21(iTime)) / iResolution.xy / 2.0));
    fragColor = mix(prv, cam, factor);
}
