void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= vec2(0.5, 0.5);
    uv *= mat2(cos(iTime), -sin(iTime), sin(iTime), cos(iTime))
    *mat2(cos(iTime), -sin(iTime), cos(iTime), sin(iTime)); 

    vec2 uv10 = uv * (10.0 * sin(iTime));
    vec3 col = vec3(0.0);
    float f = mod(floor(uv10.x) + floor(uv10.y), 2.0);
    col = mix(vec3(0.0),vec3(1.0), f);
    col -= vec3(uv.x + uv.y, uv.y, 0.0);
    fragColor = vec4(col,1.0);
}
