void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;

   vec3 color1 = vec3(0.0039, 0.2039, 0.149);
  vec3 color2 = vec3(0.2392, 0.5137, 0.8314);
  //vec3 color3 = vec3(0.7176*sin(u_time), 0.6196, 1.0);
  vec3 color4 = vec3(1.0, 1.0, 1.0);
  vec3 color5 = vec3(1.0, 1.0, 1.0);

  st = vec2(st.x * 2.0 - 1.0, st.y * 2.0 - 1.0);

  float d = length(st);
  vec3 color = mix(color1, color2, st.y/sin((0.4*d)*iTime*sin(.25*iTime)));
  color = mix(color, color4, smoothstep(0.4,0.6, d));
  color = mix(color, color5, smoothstep(0.8,1.0, d));

  fragColor = vec4(color, 1.0);
}
