uint randstate = 0u;

void randseed(uint seed) {
    randstate = seed;
}

uint randu() {
    randstate = randstate * 747796405u + 2891336453u;
    uint value = ((randstate >> ((randstate >> 28u) + 4u)) ^ randstate) * 277803737u;
    return (value >> 22u) ^ value;
}

float randf() {
    return float(randu()) / 4294967296.0;
}

vec3 randvec3() {
    return vec3(randf(), randf(), randf());
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    const int downscale = 5;
    fragCoord = vec2(ivec2(fragCoord) / ivec2(downscale) * ivec2(downscale));

    vec2 uv = fragCoord / iResolution.xy;
    vec3 color = texture(iChannel0, uv).rgb;

    const vec3 green = vec3(13, 163, 38) / vec3(255);
    bool isGreen = distance(color, green) < 0.2;

    if (isGreen) {
        randstate = uint(fragCoord.y * iResolution.x + fragCoord.x);
    } else {
        randstate = uint(fragCoord.x * iResolution.y + fragCoord.y);
    }

    /*
    randstate = uint(fragCoord.y * iResolution.x + fragCoord.x);

    if (!isGreen) {
        randstate *= uint(iTime * 100.0);
        //randvec3();
    }
    */

    fragColor.rgb = randvec3();

    //fragColor.rgb = vec3((fragColor.r + fragColor.g + fragColor.b) / 3.0);
}
