#define H(a) (cos(vec4(4.6, 3.9, 3, 1)+(a)*6.3)*.5+.5)  // hue
void mainImage(out vec4 C, vec2 U)
{
    vec2 R = iResolution.xy,
         v = (U+U-R)/R.y,
         u = v*10./(1.-iMouse.y/R.y),
         h = vec2(.5, .866), 
         a = mod(u  , h+h) - h,
         b = mod(u-h, h+h) - h,
         g = dot(a,a) < dot(b,b) ? a : b,
         k = abs(g);
    float p = pow(length(u-g), 2.)*iTime/60.;
    C = (1.-max(k.x, dot(k, h))*2.) * H(p) * (sin(p*6.3)/2.+1.) * (2.-length(v));
}
