float map(float value, float min1, float max1, float min2, float max2) {return min2 + (value - min1) * (max2 - min2) / (max1 - min1);}
float cont(float x, float y, float t) {float range = y - x;return x + mod(t - x, range);}
void mainImage( out vec4 fragColor, in vec2 p){
p /= iResolution.xy;
vec2 uv = p.xy;
vec2 mp = iMouse.xy / iResolution.xy;// float mpx = sin(iTime  /  2.);//float mpy = cos(iTime  /  2.);
/// size /= pow(uvCent / 1., size) + 1.;// size = smoothstep(size, 0.0001, uvCent + 0.3) / 1.7;//size /= pow(size, uvCent * 5.4) + 1.;//    size *= pow(size, uvCent * 5.4) + 3.3;
float pointerFact = distance(p, mp);
vec2 tileRepeat = vec2(10.0); 
// Tiling factor
vec2 cropSize = vec2(0.5 * map(pointerFact, 0.0, 1.0, 1.0, 0.7)); // Crop size
vec2 centerOffset = (1.0 - cropSize) * 0.50; // Calculate center offset// how fast you reach the corners
float fact = 1.3;
centerOffset.x = (1.0 - cropSize.x) * (clamp((p.x) * fact * pointerFact, 0. , 1.));
centerOffset.y = (1.0 - cropSize.y) * (clamp((p.y) * fact * pointerFact, 0. , 1.));
//
centerOffset*= map(pointerFact, 0.0, 1.0, 0.4, 0.6);
vec2 tiledTexCoord = fract(p * tileRepeat); 
// Apply tiling// Adjust tiled texture coordinates to crop from the center
vec2 croppedTiledTexCoord = mix(centerOffset, centerOffset + cropSize, tiledTexCoord);
fragColor = texture(iChannel0, croppedTiledTexCoord);
}
