void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_trn_pixel = fragCoord;
    vec4 o_col_last = texelFetch(iChannel0, ivec2(o_trn_pixel.xy), 0);
    fragColor = (iFrame % 2 == 0 ? o_col_last : 1.-o_col_last);
}

float f_n_rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}
#define n_tau 6.2831
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 o_trn_pixel = fragCoord;
