    //return;
    float n_rnd_fixed = f_n_rand(o_trn_pixel_nor);

    vec4 o_col_last_sum = vec4(0);
    vec4 o_col_last_min = vec4(1.);
    ivec2 o_scl_krn = ivec2(3.+n_rnd_fixed*4.);
    float n_min = 4.;
    for(int n_x = 0; n_x < o_scl_krn.x; n_x+=1){
        for(int n_y = 0; n_y < o_scl_krn.x; n_y+=1){
            ivec2 o_trn = ivec2(o_trn_pixel.xy)+ivec2(n_x, n_y)-ivec2(o_scl_krn/2);

            o_trn += ivec2(round(o_trn_mouse_nor*2.));
