            float n = o_col_last.x + o_col_last.y + o_col_last.z + o_col_last.w;
            if(n < n_min){
                n_min = n; 
                o_col_last_min = o_col_last;
            }
            o_col_last_sum+=o_col_last;
        }
    }

    vec2 o_diff = o_trn_mouse_nor.xy-o_trn_pixel_nor.xy;
    float n_dist = 1.-length(o_diff);
    float n_ang = atan(o_diff.y, o_diff.x)/n_tau+.5;

    vec4 o_col_last_sum_nor = o_col_last_sum / vec4(o_scl_krn.x * o_scl_krn.y);
    float n_min_nor = n_min / 4.;
