    //return;
    if(o_col_last_sum_nor.x <= (sin(n_rnd))){
        fragColor = vec4(1.-n_rnd_fixed*.1);
    }

    //float n_white_sphere = pow(n_dist, 2./(sin(iTime)*.5+.5))*1.1;
    float n_white_sphere = pow(n_dist,2.);
    if(n_white_sphere > .5){
//        fragColor = vec4(n_white_sphere);//vec4(n_white_sphere)+n_rnd;
    }


}

vec2 f_o_trn_nor( vec2 o , vec3 iResolution)
{
    return (o.xy / iResolution.y)-0.5;
}
