#define sat(a) clamp(a, 0., 1.)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;

    vec2 mouse = iMouse.xy / iResolution.xy;

    float r = clamp(-0.25, 0.25, mouse.y);
    float outsideL = 0.5;
    float insideL = outsideL - r;

    vec2 maskxy = step(abs(uv), vec2(insideL));
    float mask = maskxy.x + maskxy.y;
    vec2 ruv = mod(abs(uv), insideL);

    float maskCorner = sat(step(length(ruv), r) + mask);
    maskxy = step(abs(uv), vec2(outsideL));
    mask = maskxy.x * maskxy.y;

    float alpha = maskCorner * mask;

    vec3 col = vec3(1., 0., 0.) * alpha;

    fragColor = vec4(col,1.0);
}
