void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


   float timeMod = 2.;
   float zoom = 7.*(cos(iTime/(4.*timeMod))+ 2.);
   float xShift = 0.25*zoom;
   float yShift = 0.3*zoom;


   float x = (fragCoord.x/iResolution.x*zoom)-xShift;
   x = (sin(iTime/(2.*4.*timeMod)) + 1.)/2.*x;
   float y = (fragCoord.y/iResolution.y*zoom/1.5)-yShift;
   y = (cos(iTime/(2.*3.*timeMod)) + 1.)/2.*y;


   float n = 4.;
   for(float i = 0.; i < n; i++){


       float yOld = y; 
       y = x*y*2.;
       x = (x*x) - (yOld*yOld);
       y = y + (fragCoord.y/iResolution.y*zoom/2.)-yShift;
       x = x + (fragCoord.x/iResolution.x*zoom)-xShift;

       yOld = y - 1.*x;
       y = 1.0*(cos(y*2.) + 2.)*(cos(iTime/(3.1415*1.*timeMod) + 1.)) + 0.5;
       x = sin(yOld);
       x = x*y;


       x = (sin(iTime/(2.*timeMod)) + 2.)*2.25*sin(x) + 0.5;
       x = 1.0*(cos(x)+1.);
       x = x*x/5.0;
       //y = y*x*2.;




   }

   fragColor = vec4(log(x+y)*log(x+y)/1.0, log(abs(x+(y/4.)))*1.0*log(x+y), log(abs(y-(x/2.))/4.)*log(abs(y-x))*1., 1.0);

}
