vec3 palette (float t) {
    vec3 a= vec3(3.138, 0.758, 1);
    vec3 b= vec3(3.138, 0.048, 0.500);
    vec3 c= vec3(0.358, 4, 0.718);
    vec3 d= vec3(0.498, 0.333, 0.008);

    return a + b*cos(23.*(c*t+d));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ((fragCoord*2.0-iResolution.xy))/iResolution.y*1.5;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for (float i=0.0; i<3.;i++){

        uv = fract(uv*1.)- 0.5;

        float d = length (uv)*exp(length(uv0));
        vec3 col = palette(length(uv0)+iTime*0.1+ i*0.4);

        d = (sin(d*3. + iTime))/8.;
        d = abs(d);
        d = pow(0.007 /d ,2.2);

        finalColor += col * d;

        }
    // Output to screen

    fragColor = vec4(finalColor,1.0);
}
