float easeInQ(float x) {
    return pow(x, 6.0 * (cos(iTime) + 2.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    uv *= 10.0 * iTime;
    vec2 pid = floor(uv);

    fragColor = vec4(vec3(easeInQ(abs(sin(iTime * 4.0 + abs(pid.x * pid.y))))),1.0);
}
