float julia(vec2 uv, float angle, float MAX_ITERATIONS)
{
    vec2 c = vec2(0.7885 * cos(angle), 0.7885 * sin(angle));
    vec2 z = 3.0 * uv - vec2(0.7, 0.0);
    float iteration = 0.0;
    for (float i = 0.0; i < MAX_ITERATIONS; i++)
    {
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;
        if (dot(z, z) > 4.0)
            return iteration / MAX_ITERATIONS;
        iteration++;
    }
    return 0.0;
}



vec3 pal(in float t)
{
    vec3 a = vec3(0.5,0.5,0.5);
    vec3 b = vec3(0.5,0.5,0.5);
    vec3 c = vec3(1.0,0.7,0.4);
    vec3 d = vec3(0.0,0.15,0.20);

    return a + b * cos(6.28318 * (c * t + d));
}


const float PI = 3.1415926;
const float TAU = 2.0 * PI;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 resolution = iResolution.xy;
    float time = iTime;
    const float MAX_ITERATIONS = 64.0;

    float offsetX = 0.335; 

    vec2 uv = (fragCoord - vec2(offsetX * resolution.x, 0.5 * resolution.y)) / resolution.y;
    uv *= 0.8;
    vec3 col = vec3(0.0);

    float t = fract(time * .5 / TAU); 
    float angle = mix(0.0, TAU, t); 


    float m = julia(uv, angle, MAX_ITERATIONS);

    col += pal(m);

    fragColor = vec4(col, 1.0);
}
