void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Parametry spiralnego efektu
    float speed = 0.5;       // Prędkość obrotu spirali
    float frequency = 100.0;   // Częstotliwość zmiany kolorów
    float amplitude = 0.3;   // Amplituda zmiany kolorów

    // Normalizacja współrzędnych pikseli do zakresu od -1 do 1
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    // Oblicz kąt dla każdego piksela
    float angle = atan(uv.y, uv.x);

    // Oblicz promień dla każdego piksela
    float radius = length(uv);

    // Oblicz przesunięcie czasowe dla efektu animacji
    float timeOffset = iTime * speed;

    // Oblicz zmianę kolorów na podstawie kąta, promienia i czasu
    vec3 color = vec3(
        0.10 + 0.5 * sin(angle * frequency + timeOffset),
        0.5 + 0.5 * sin(radius * frequency + timeOffset),
        0.5 + 0.5 * sin((angle + radius) * frequency + timeOffset)
    );

    // Dodaj efekt zmiany kolorów do fragColor
    fragColor = vec4(color, 1.0);
}
