vec3 palette( in float t ) {
    vec3 a = vec3(0.8, 0.5, 0.4);
    vec3 b = vec3(0.2, 0.4, 0.2	);
    vec3 c = vec3(2.0, 1.0, 1.0);
    vec3 d = vec3(0.00, 0.25, 0.25);

    return a + b*cos( 6.28318*(c*t+d) );
}

float ringsCount = 20.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;
    float d0 = length(uv);


    uv = fract(uv / ((d0 + 1.) * 0.3)) - .5;



    float d = length(uv);


    uv = fract(uv * (d0 + 2.)) - .5;

    d0 -= .7 + sin(iTime * .4) / 1.5;
    //d0 = abs(d0);
    d0 = 0.08 / d0;

    d = fract(d - d0) * 2.;

    d = fract(d - d0);
    d = sin((d * ringsCount) + 3.) / ringsCount;
    d = abs(d);
    d = ((sin((iTime) * 1.5) + 1.8) * 0.003) / d;

    vec3 col = vec3(palette(length(uv) + d0 + (iTime * .4)));



    col *= d - ((d0 + cos(iTime * 2.)) * 0.3);

    //col = vec3(d0);

    //col = vec3((((d0) + cos(iTime * 2.)) * 0.3));
    fragColor = vec4(col,1.0);
}
