//https://iquilezles.org/articles/palettes/
vec3 palette( float t ) {
    vec3 a = vec3(0.657, 0.690, 0.569);
    vec3 b = vec3(0.619, 0.117, 0.156);
    vec3 c = vec3(1.304, 0.977, 0.246);
    vec3 d = vec3(0.974, 4.653, 2.999);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 16.0; i++) {
        uv = fract(uv * 1.1) - 0.5;

        float d = length(uv) * exp(-length(uv0));

        vec3 col = palette(length(uv0) + i*0.14 + iTime* 0.1);

        d = sin(d*32. + iTime * 0.5)/32.;
        d = abs(d);

        d = pow(0.01 / d, 1.1);

        finalColor += smoothstep(0.05,0.95, col * d) * 0.01 * i;
    }

    fragColor = vec4(finalColor, 1.0);
}
