vec3 palette( float t ) {
    vec3 a = vec3(2.108, 0.500, 0.500);
    vec3 b = vec3(0.500, 0.500, 0.500);
    vec3 c = vec3(1.000, 1.000, 1.000);
    vec3 d = vec3(0.000, 0.333, 0.667);

    return a + b*cos( 6.28318*(c*t+d) );
}
float dot2( in vec2 v ) { 
return dot(v,v); 
}
float sdHeart( in vec2 p ){
    p.x = abs(p.x);

    if( p.y+p.x>1.0 )
        return sqrt(dot2(p-vec2(0.25,0.75))) - sqrt(2.0)/4.0;
    return sqrt(min(dot2(p-vec2(0.00,1.00)),
                    dot2(p-0.5*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0 - iResolution.xy)/iResolution.y;
    uv.y+=0.5;
    vec3 col;
    vec3 color1=palette(length(uv)+iTime*.4);
    vec3 color2=vec3(0.0,0.0,0.0);


    float d = sdHeart(uv);
    d=sin(d*2.5+iTime*4.00)/5.00;

    col = (d>0.0) ? color1 : color2;
    col *= 1.0 - exp(-10.00*abs(d));
    col *= 1.0 + 1.0*cos(iTime*1.00+128.0*abs(d));
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.00,0.01,abs(d)) );



    fragColor = vec4(col, 1.0);
}
