float scl = 100.;

float noise1(in float v,float scl){
	return fract(sin(v*scl)*scl);
}
float sdfCircle(vec2 uv, vec2 pos,float rad){
    return length(uv-pos)/rad;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    scl-=.01*iTime;
    vec2 uv = fragCoord/iResolution.x;
    float pc = atan(uv.x-.5,uv.y-.5) + .26;
    vec3 col = vec3(noise1((iTime*.1)+uv.x,scl)+pc+noise1((iTime*.1)+uv.y,scl)-noise1((iTime*.1)-uv.y,scl));
    col *= 1.-sdfCircle(uv,vec2(.5,.2),.1);
    col *= 1.-sdfCircle(uv,vec2(.5,.2+sin(iTime*.03*.4)),.5);
    col = fract(col*vec3(1.,2.,3.))-.5;
    fragColor = vec4(col,1.0);
}
