// Golfing 775 chars "random lozenge tiling" by jt. https://shadertoy.com/view/ctyyz1

// ---  -25 ch by Xor

void mainImage( out vec4 o, vec2 u ) {
    vec3  R = iResolution,
          t = ceil(  mat2x3(4,4,-8,-7,7,0)  * u*2. / R.y );
    o.rgb = 1. - mod( t - t.yzx  // ← faceId         ↓ hexagonId
                      - ceil(cos(iTime +2e4*sin(dot(ceil((t.zxy-t)/3.),R)))), 3.); 
}






