vec2 f1(vec2 a){
    return
        fract(a)
    ;
}

vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return
        abs(f1((a1)*(a2.x+a2.y))-.5)
    ;
}

#define triwave_(p) abs(fract(.5+p/4.0)-.5)*2.
#define triwave1_(p) (abs(fract(p/8.0)-.5)-abs(fract(p/2.0)-.5)/2.)
float t1_(vec2 c, vec2 p){
    return triwave_((.5 - length( min(p=fract(p*sign(triwave1_(c*c.y))), 1.-p.yx) )));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.;
    vec2 draggedPos = texelFetch(iChannel0, ivec2(1,0), 0).xy;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    uv -= draggedPos/iResolution.y/t1;
    vec2 t2 = vec2(0.);
    bool b = uv.x>uv.y;

    for(int k = 0; k < 6; k++){

        //uv.x -= t1_(floor(uv),floor(uv));
        //uv -= triwave1_(uv);


        uv =
            abs(.5+uv+t2)
        ;
        //uv = abs(fract(vec2(uv+vec2(.5,1.5))*1.5)-.5)/1.5;

        b =
            uv.y>uv.x
        ;
        if(b)
        uv =
            uv.yx
        ;
        t2 =
            -triangle_wave(uv-.5)
        ;
        uv =
            t2-triangle_wave(uv.yx)
        ;
        col =
            vec3(col.zx,max(uv.x-uv.y,0.))
        ;
        uv +=
            float(uv.x>uv.y)/1.5
            //float(uv.x>uv.y&&t2.x>t2.y)/1.5
            //float(t2.x>t2.y)/1.5
            //float(uv.x-uv.y)/1.5
        ;
    }
    fragColor = vec4(col*2.,1.0);
}

float fmod(float a, float b){
    a +=
        floor(mod(b/2.,2.))
    ;
    /*
    for(float i = 1.; i < mod(floor(a),8.); i++){
        a += mod(floor(a/8.),b)*i;
    }
    */
    return
        mod(mod(floor(a),b)*mod(floor(a/(b)),b),b)
    ;
}

float prime_rhythm(float time){
    //time += mod(time,5.)-mod(time,3.);
    return
        (1.+fmod(time/4.,2.))/(1.+fmod(time,2.))
    ;
}

//change the notes without changing the rhythm
float fmod1(float a, float b){
    return
        //fmod(a,b)
        fmod(a+floor(a/4./4.),b)
        //max(fmod(a+floor(a/4./4.),b),fmod(a+floor(-a/4./4.),b))
        //fmod(a,b-fmod(a/2.,2.))
        //fmod(floor(a)*5.,b+fmod(floor(a/8.)*3.,b))
    ;
}

