vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 c, in vec2 crd )
{
    vec2 uv = (crd * 2.0 - iResolution.xy) / iResolution.y;
    vec2 nuv = normalize(uv);


    float dist = 1.0f;
    float iterations = 20.0;
    for(float i = 0.0;i < iterations;i++) {   
        float t = iTime - i * 0.02;
        float scale = 2.0 / (3.0 - cos(2.0*t));
        vec2 point = vec2(scale * cos(t), scale * sin(2.0 * t));
        vec2 n = normalize(point - uv);
        dist = min(dist, length(uv - point + n * 0.004 * (iterations - i)));
    }


    dist = smoothstep(0.0, 0.15, dist);
    dist = 0.1 / dist;

    vec3 color = palette(iTime * 0.1, vec3(0.5, 0.5, 0.5), vec3(0.5, 0.5, 0.5), vec3(1.0, 1.0, 1.0), vec3(0.00, 0.33, 0.67));
    color *= dist;
    c = vec4(color, 1);
}
