void mainImage( out vec4 O, in vec2 I )
{
    vec2 uv = (2.0*I-iResolution.xy)/iResolution.y;
    float t = iTime;
    uv.x = abs(uv.x);

    float f = 1.8/length(uv);
    uv = uv * f - vec2(f + iTime*20.,0.5);
    uv.x /= 10.0;
    vec3 tex = texture( iChannel0, uv ).xyz;
    uv = uv-tex.xy;
    tex = texture( iChannel0, uv ).xyz;
    vec3 ref = reflect(tex, vec3(uv,0.0));
    vec3 col = tex*f;

    ref = texture( iChannel1, vec3(uv,0.0) ).rgb;

    float dark = 1.0;

    if( iTime < 20.5 ){
        dark = 0.2+iTime/42.0;
        dark *= dark*dark;
    }    
    else if( iTime > 20.5 && iTime < 22.7 ){
        dark = 0.7+(20.5-iTime)*0.25;
        dark *= dark*dark;
    }
    else if( iTime > 22.5 ){
        dark = 1.5;
        dark *= dark*dark;
    }

    col *= ref*(1.5+0.5*sin(iTime*2.0+cos(iTime*0.3)))*dark;
    O = vec4(col,1.0);
}
