void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/ iResolution.xy;

    vec2 wavedUv = vec2(
        uv+ sin(uv.yx * 80.0 + iTime * 10.0) * .1
    );

    float strength = smoothstep(0.02, 0.0, abs(distance(wavedUv, vec2(0.5)) - 0.25)); // anti aliasing

    fragColor = vec4(strength);
}
