void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Definir tamanhos e posições dos objetos
    vec2 objectPositionX = vec2(sin(iTime), 0.0) * 0.5;
    vec2 objectPositionY = vec2(0.0, cos(iTime)) * 0.5;
    float objectSize = 0.3;

    // Verificar se o pixel está dentro de um dos objetos
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    vec3 color = vec3(0.0);

    if (length(uv - objectPositionX) < objectSize)
    {
        color = vec3(1.0, 0.0, 0.0); // Cor do objeto X (vermelho)
    }
    else if (length(uv - objectPositionY) < objectSize)
    {
        color = vec3(0.0, 1.0, 0.0); // Cor do objeto Y (verde)
    }
    else
    {
        color = vec3(0.1, 0.2, 0.2); // Cor do fundo
    }

    fragColor = vec4(color, 1.0);
}
