void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float r = 20.0;
    vec2 gridSize = (iResolution.xy) / vec2(r);
    float halfY = gridSize.y * 0.5;
    vec2 cell = floor(gl_FragCoord.xy / gridSize);
    vec2 center = (cell + vec2(0.5)) * gridSize;
     float radius = mod(cell.x + cell.y, 2.0) < 1.0 ? (sin(iTime) * 0.5 + 0.5) * halfY: (cos(iTime) * 0.5 + 0.5) *  gridSize.y * 0.45;

    vec4 color = vec4(0.10, 0.10, 0.10, 1.0);
     float dist = length(gl_FragCoord.xy - center);

    if(distance(center, iMouse.xy) < halfY) {

        radius = halfY;



    }

    if (dist < radius) {
        color += vec4(cell.x / r, cell.y / r, 0.95, 1.0);
    }

    fragColor = color;
}
