#define R iResolution.xy
#define T (iTime)
#define M_PI (3.1416)
#define TAU (M_PI * 2.0)

float hash21(vec2 p) {
    float n = 0.0;
    n += textureLod(iChannel3, p, ((0.5+(0.5*sin(T)))*3.)*0.5).r;
    n += textureLod(iChannel2, p, ((0.5+(0.5*sin(T)))*3.)*0.5).r;
    n += textureLod(iChannel1, p, ((0.5+(0.5*sin(T)))*3.)*0.5).r;
    n += textureLod(iChannel0, p, ((0.5+(0.5*sin(T)))*3.)*0.5).r;
    return n / 4.0;
}


vec3 path(vec2 p) {
    p = abs(p);
    float a = hash21(((p)*0.006) + vec2(cos(T*0.02), T*0.1));
    float x = cos(a*TAU);
    float y = sin(a*TAU);

    p += vec2(x, y)*8.;

    float v = clamp((1.0 / pow(length(p), 1.6)) * 8.0, 0.0, 1.0);

    float c = hash21(p*0.001)*2.0-1.0;
    //v = pow(v, 1.6);
    v = clamp(v, 0.0, 1.0);
    return vec3(v) * abs(vec3(cos((c+a)*TAU), sin((c+a)*TAU), tan((c+a)*TAU)));
}


mat2 rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, s, -s, c);
}

void mainImage( out vec4 O, in vec2 fc )
{
    vec3 col = vec3(0.0);
    vec2 uv = (fc-0.5*R.xy)/R.y;
    vec3 rd = normalize(vec3(uv.xy, 1.0));
    vec2 m = (iMouse.xy-0.5*R.xy)/R.y;

    if (iMouse.z > 0.01) {
        rd.yz *= rot(m.y*TAU);
        rd.xz *= rot(m.x*TAU);

    }

  //  uv *= 8.0;

 //   uv /= (rd.y*8.);


    float h = hash21(vec2(((rd.x/rd.y)*0.005), (T*0.005)+(rd.z*0.1)));

    h = h*2.0-1.0;
    h *= 0.16;

    col += path(rd.xz/(rd.y+h));

    O = vec4(col, 1.0);
}
