float random(float st){
    return fract(cos(st)*35523445.323);
}

vec2 random2(vec2 st){
    float A = dot(st, vec2(30.0349593));
    float B = dot(st, vec2(80.304943));
    return fract(cos(vec2(A, B)) * 3495032.34);
}

float wave(vec2 stFloor, vec2 stFract, float ran, float velocity){
float sdf = 1.;
     for(int x = -1; x <= 1;x++){
        for(int y = -1; y <= 1; y++){

            vec2 neighbour = vec2(float(x), float(y));
            vec2 point = random2( neighbour+stFloor + ran);
            point = 0.5 + 0.5*cos((iTime*(1.*velocity)) + 6.754 * point);
            vec2 diff = point + neighbour - stFract;
            float c = length(diff);

            sdf = min(sdf, c);
        }
    }
    return sdf;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;

     st.x *= iResolution.x/iResolution.y;
    st*= 3.;

    vec2 c_Fl = floor(st);
    vec2 c_Fr = fract(st);

    float d_min = wave(c_Fl, c_Fr, 0., 1.2);
    float d_min2 = wave(c_Fl, c_Fr, 3452.34543, 0.5);
    float d_min3 = wave(c_Fl, c_Fr, 443.4342245, 0.4);
    float d_min4 = wave(c_Fl, c_Fr, 344344.43434, 0.7);

    vec3 color = vec3(0.457,0.470,0.945);
    color = mix(color, vec3(0.070,0.975,0.759), d_min);
    //bright
    color = mix(color, vec3(0.), abs(cos(iTime/5.)));
    color = mix(color, vec3(0.708,0.768,0.835), d_min2);
    //bright
    color = mix(color, vec3(0.), 0.232);
    color = mix(color, vec3(0.181,0.791,0.980), d_min3);
    //bright
    color = mix(color, vec3(0.), 0.132);
    color = mix(color, vec3(0.481,0.791,0.980), d_min4 * 0.508);

    // Output to screen
    fragColor = vec4(color,1.0);
}
