float hash(vec2 uv) {
   const float A = -17273.0089283;
   const float B =  84175.1827736; 
   float x = cos(fract(mix(A,B,fract(uv.x*1.73627))*10.)*10.91873);
   float y = sin(fract(mix(A,B,fract(uv.y*1.62931))*10.)*10.82772); 
   float z = sin(atan(x,y)*3.91737);
   return fract(sin(z*33.92773*fract(77.91828300182*tan(1150.8776191801*dot(uv,vec2(x,y))))));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x += cos(iTime*0.1);
    // Time varying pixel color
    vec3 col = vec3(hash(uv*0.000001));
    // Output to screen
    fragColor = vec4(col,1.0);
}
