void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e;
o=vec4(.0);

for (i=0.;i++<64.&&d>1e-4;){
    vec3 p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e);
    vec3 q=vec3(log(length(p))-iTime, cos(p.y), atan(p.y,p.z));

    for(j=0.;j++<16.;)
        p=abs(abs(p)-.01)-.01,
        d=pow(dot(cos(q),sin(q)),3.5),
        d+=max(max(p.x,p.z),p.y),
        d-=dot(p,p)/2e4;
    e+=d;
}o+=3./i;
}
