/* This animation is the material of my first youtube tutorial about creative 
   coding, which is a video in which I try to introduce programmers to GLSL 
   and to the wonderful world of shaders, while also trying to share my recent 
   passion for this community.
                                       Video URL: https://youtu.be/f4s1h2YETNY
*/

//https://iquilezles.org/articles/palettes/
vec3 palette( float t ) {
       vec3 a = vec3(0.341,0.867,1.000);
    vec3 b = vec3(0.255,0.439,0.984);
    vec3 c = vec3(220, 122, 162);
    vec3 d = vec3(0.333,0.925,0.969);


    return a + b*cos( 6.28318*(c*t+d) );
}

const float EPSILON = 0.001;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.3 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    float timeFactor = iTime * 0.5;  // Adjust the speed of motion

    for (float i = 0.0; i < 5.0; i++) {
        uv = fract((uv + vec2(cos(timeFactor), sin(timeFactor))) * 1.5) - 0.5;  // Add motion to UV

        float d = length(uv) * exp(-length(uv0));

        vec3 col = palette(length(uv0) + i * 0.4 + timeFactor * 0.4);

        d = sin(d * 8. + timeFactor) / 6.;
        d = abs(d);

        d = pow(0.01 / d, 2.2);
        vec3 pos = vec3((uv - 0.5) * 2.0 * 0.7, timeFactor * 2.0);  // Adjust position over time
        float c = 16.0 / (pos.z - timeFactor * 2.0 + 16.0);
        fragColor = vec4(0.1, 0.7, 1, 1) * c + vec4(0, 0.1, 0.1, 1) * (1.0 - c);

        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 1.0);
}
