void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    uv = uv - mod(uv, 20./iResolution.y);
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec2 st = sin(cos(fragCoord/iResolution.x * 20.) + iTime);
    float r = length(st-0.5);
    col = col * pow((smoothstep(0., 0.45, r) - smoothstep(0.45, 1., r)), 100.);
    //col = col * pow(smoothstep(0.,1., r),10.);
    //if (r > 0.5 || r < 0.4) {col = vec3(0.);}

    // Output to screen
    fragColor = vec4(col,1.0);
}
