vec2 inRes;
const float PIXEL_SIZE = 3.0;
const float HEIGHT_SCALE = 1.0;
vec3 lightPow = vec3(1000, 1000, 1000);
vec3 lightPos = vec3(-12.5, 14.5, 12.5);
vec3 camPos = vec3(0.5, 5, 0.5);

vec4 GetColor(in vec2 fragCoord) {
    vec2 uv = fragCoord * inRes;
    vec4 cc = texture(iChannel0, uv);
    vec2 offset = vec2(cos(cc.a * TAU * 2.0), sin(cc.a * TAU * 2.0)) / iResolution.xy;        
    vec4 cc1 = texture(iChannel1, cc.xy * offset);
    vec4 cc2 = texture(iChannel2, cc.xz + cc.yz + offset);
    return sqrt((cc1 * cc1 + cc2 * cc2) * 0.5) ;

}

float GetLevel(vec4 col) {

    return (col.x + col.y + col.z + col.a) * HEIGHT_SCALE;
}

vec4 PixelColor(in vec2 fragCoord) {
    vec2 uv = fragCoord * inRes;
    vec4 colA = GetColor(fragCoord);


    float A  = GetLevel(colA);
    float B = GetLevel(GetColor(fragCoord + vec2(PIXEL_SIZE,  0)));
    float C = GetLevel(GetColor(fragCoord + vec2(0,  PIXEL_SIZE))); 
    vec3 norm = normalize(cross(vec3(1.0, B - A, 0), vec3(0, C - A, 1.0)));
    vec3 L = vec3(uv.x, A, uv.y) - lightPos;
    float dist = length(L);
    L = normalize(L);
    vec4 l = vec4((lightPow/ (dist * dist)) * clamp(dot(norm, L), 0.0, 1.0), 0) ;
    vec3 R = reflect(L, norm);
    float spec = pow(clamp(dot(normalize(camPos), R), 0.0, 1.0), 15.0);
    return colA * l + vec4(spec) * (1.0 - A * 0.25);

}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    inRes = 1.0 / iResolution.xy;

    fragColor = pow(PixelColor(fragCoord), vec4(1.2));  // push up the contrast a bit
}

#define MAX_POINTS 100u
#define MAX_POINTS_F 100.0
#define INV_11 (1.0 / 11.0)

const float PI = 3.14159;
const float TAU = PI * 2.0;
const float ANG_90 = PI * 0.5;
const float INV_PI = 1.0 / PI;
const float INV_ANG_90 = 1.0 / ANG_90;
const float INV_TAU = 1.0 / TAU;
const float INV_60 = 1.0 / 60.0;
const float INV_64 = 1.0 / 64.0;
const float INV_255 = 1.0 / 255.0;


#define MIN_SEED 0x1FFu
const float INV_RND_RANGE = 1.0 / float(0xFFFFFFFFu);
uint seed = MIN_SEED;
uint Rnd() { seed ^= seed << 13; seed ^= seed >> 17; seed ^= seed << 5; return seed; }
uint Rnd(in uint range) { return Rnd() % range; }
float RndUnit() { return float(Rnd()) * INV_RND_RANGE; }
vec2 RndCoord() { return vec2(RndUnit(), RndUnit()); }
bool Odds(in uint oneIn) { return (Rnd() % oneIn) == 0u; }
void Seed(in uint val) { seed = MIN_SEED + val; }
mat2 Rot2D(float ang) { return mat2(cos(ang), sin(ang), -sin(ang), cos(ang)); }

vec4 colorRef[11] = vec4[](
    vec4(0.0,  0.0,  0,  INV_11 * 0.0),
    vec4(0.1,  0.1,  0,  INV_11 * 1.0),
    vec4(0.2,  0.2,  0,  INV_11 * 2.0),
    vec4(0.3,  0.3,  0,  INV_11 * 3.0),
    vec4(0.4,  0.4,  0,  INV_11 * 4.0),
    vec4(0.5,  0.5,  0,  INV_11 * 5.0),
    vec4(0.6,  0.6,  0,  INV_11 * 6.0),
    vec4(0.7,  0.7,  0,  INV_11 * 7.0),
    vec4(0.8,  0.8,  0,  INV_11 * 8.0),
    vec4(0.9,  0.9,  0,  INV_11 * 9.0),
    vec4(1.0,  1.0,  0,  INV_11 * 10.0)
);
vec4 colorSet[11] = vec4[](
    vec4(1,  0,  0,  1),
    vec4(1,  0.5,0,  1),
    vec4(1,  1,  0,  1),
    vec4(0,  1,  0,  1),
    vec4(0,  1,  1,  1),
    vec4(0,  1,  0.5,1),
    vec4(0,  0.5,1,  1),
    vec4(0,  0,  1,  1),
    vec4(0.5,0,  1,  1),
    vec4(1,  0,  1,  1),
    vec4(1,  0,  0.5,1)
);

vec4 color;
float IsNear(uint idx, float mdist, vec2 xy) {
    float dist = distance(xy, RndCoord());
    if (dist < mdist) {
        color = colorRef[idx % uint(colorSet.length())];
        color.a = float(idx) / MAX_POINTS_F;
        return dist;
    }
    return mdist;

}
vec4 Vonoroi(vec2 xy, float dist) {
    color = vec4(0);
    uint idx = 0u;
    while (idx < MAX_POINTS) {
        dist = IsNear(idx++, dist, xy);
    }
    return color;
}

#define RESET 1500
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    ivec2 px = ivec2(fragCoord);
    if (px.x == 0 && px.y == 0 && (iFrame % RESET) == 0) {

        if (iFrame  % RESET == 0) {
            fragColor = vec4(0,0,0,1);
        } else {
            fragColor = texelFetch(iChannel0, ivec2(0,0), 0);
        } 

    } else if ((iFrame % RESET) == 1) {
        Seed(uint(iResolution.x * iResolution.y) + uint(iFrame));
        vec4 dd = texelFetch(iChannel0, ivec2(0,0), 0);
        float dist = dd.x;
        fragColor = Vonoroi(fragCoord / iResolution.xy, 1.0);
    } else {
        float turn = sin(float(iFrame - 100) / 100.0) * PI;
        vec4 cc = texelFetch(iChannel0, px, 0);
        const float d1 = 1.1;
        const float d2 = 3.2;
        vec4 cc1 = texture(iChannel0, (fragCoord + vec2(cos((cc.g * 0.5 + cc.a * 0.5) * TAU + turn) * d1, sin((cc.g * 3.0 + cc.a* 7.0) * TAU + turn) * d1)) / iResolution.xy);
        vec4 cc2 = texture(iChannel0, (fragCoord + vec2(cos((cc.b + cc.a) * TAU + turn) * d2, sin((cc.b + cc.a)  * TAU + turn) * d2)) / iResolution.xy);

        fragColor = vec4(sqrt((
                cc.rgba  * cc.rgba  + 
                cc1.rgba * cc1.rgba + 
                cc2.rgba * cc2.rgba 
            ) * (1.0 / 3.0)));
    }
}
