#define M_PI 3.1415926535897932384626433832795
//http://dev.thi.ng/gradients/
//iquilezles.org/articles/palettes/
//[[0.250 -0.332 -0.392] [0.740 0.638 0.808] [0.668 0.778 1.498] [0.698 0.638 0.808]]
vec3 palette( in float t)
{
   vec3 a = vec3(0.250, -0.35, -0.35);
   vec3 b = vec3(0.85, 0.7, 0.8);
   vec3 c = vec3(0.4, 0.9, 0.7);
   vec3 d = vec3(.8, .7, .45);
   return a + b*cos( 6.28318*(c*t+d) );
}
#define num_split 6.
#define wiggly 200.
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    float c =  atan(uv.y, uv.x) / (M_PI * 2.);
    float d = sqrt(length(uv) * 3.8);
    float d_t = sin(d * .5 * ((sin(iTime * .1) - 1.01) ));
    float zoom = (cos(iTime * .1)+5.- sqrt(0.5*d)) * ( sin(iTime * .1) + 1.8) ;
    float p = (sin(wiggly / zoom) / 3.) - 0.5;
    float t = (sin(iTime) + iTime) / 15.;
    c += p * sqrt(d) + t;
    c = fract(c * num_split); // create divisions in the gradient
    c = c * (sqrt(fract(d_t)) / 1.5) - pow(d/2.2,2.);
    //dithering
    //down sample the texture for pixel art feel
    vec2 smallCoord = floor(fragCoord / 0.5 + sin(iTime));
    //every so often, choose a pixel to alter
    if(int(smallCoord.x + smallCoord.y * 3.3 + sin(iTime))  % (abs(int( 3. * floor(d_t))) + 5) == 0 )
    {
      c *= 1.2;
    }
    // Output to screen
    fragColor = vec4(palette(c),1.0);
}
