#define UV_ORIGIN 0.5
#define ZOOM 1.0
#define SPEED 3.0
const float PI = 4. * atan(1.), TAU = 2. * PI;
mat2 r(float angle)
{
	angle *= PI / 180.0;
    float s=sin(angle), c=cos(angle);
    return mat2( c, -s, s, c );
}

float b( in vec2 p, in float r )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - r;
    p.y = p.y + r/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0*r, 0.0 );
    return -length(p)*sign(p.y);
}
void mainImage( out vec4 O, in vec2 F )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 R = iResolution.xy,
         u = (F+F-R) / R.y;
    vec2 u0 = u;
    float i, l = length(u);

    for(O *= i ; i++ <= 2.;
        O += pow(.04 /
                 abs(
                     sin(
                         8. * ( length ( u = fract( u * 1.5) - .5 ) - b(u0, 3.) / exp(l) )
                         + iTime*0.75
                         )//sin
                    )//abs
              , 1.2)//pow
              * (1. + cos(6. * ( l + (i + iTime)*.4 + vec4(6.5,.5,6.9,0) )))
    );              
}
