void mainImage( out vec4 O, vec2 F )
{
    vec2  R = iResolution.xy,
          u = ( F+F - R ) / R.y, P,z;
    float l = length(u), t = iTime, i,T;

    for( ; i++ < 4.; ) 
       u = fract( u * 1.5* mat2(cos(t*.058 +vec4(0,11,33,0))) ) - .5; 

    P = abs( u );
    P = vec2( abs(P.x-P.y), 1.-P.x-P.y ) *.707;

    float h = 4.,
          p = ( h - P.y -.25/h )/(6.*h),
          q = P.x / (h*h*16.),
          H = q*q - p*p*p, x,r;
    if( H>0. ) r = sqrt(H), x = pow(q+r,1./3.)-pow(abs(q-r),1./3.)*sign(r-q); 
    else       r = sqrt(p), x = 2.*r*cos(acos(q/(p*r))/3.);

    z = vec2( x, h -2.*h*min(x*x,2.) ) - P;
    T = length(z) * sign(z.y);
    h = sin(3.8 * ( length(u) - T - t*.1));
    H = cos(3.8 * ( length(u) - T - t*.1)) * 3.8 * ( 30./R.y  );  // derivative(h), but fwidth() would get artifacts
    O = clamp(1. - abs(tan( 3.8 * ( length(u) - T - t*.1) ) * R.y/114. ), 0.,1. ) 
       * (1. + cos(5. * ( l + (4.+t)*.1 + vec4(6.5,.5,6.9,0) )))/2. ; 
}
