// https://www.shadertoy.com/view/dtcyWs random domino merge align tiling, 2023 by jt
// based on https://www.shadertoy.com/view/mtcyDl random domino merge square debug

// Tiling of randomly (horizontally/vertically) aligned pairs of domino tiles
// (https://en.wikipedia.org/wiki/Domino_tiling)
// with dominos merged into squares across tile boundaries.
// Click to display domino tile outlines.

// tags: random, tiling, squares, tesselation, merge, combine, align, domino, adjacent

// The MIT License
// Copyright (c) 2023 Jakob Thomsen
// Permission is hereby granted, free of charge, to any person obtaining a copy of this software and associated documentation files (the "Software"), to deal in the Software without restriction, including without limitation the rights to use, copy, modify, merge, publish, distribute, sublicense, and/or sell copies of the Software, and to permit persons to whom the Software is furnished to do so, subject to the following conditions: The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software. THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS IN THE SOFTWARE.
// https://www.shadertoy.com/view/DlcyWs alternating domino tiling, 2023 by jt

float hash12(vec2 p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

vec3 hash32(vec2 p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

bool random_domino_pair_tiling_flip(ivec2 tile) // https://www.shadertoy.com/view/ct3cWs random domino pair tiling (jt)
{
    return hash12(floor(iTime/3.0)+vec2(tile)) < 0.5;
}

ivec2 random_domino_tiling_merge_squares_across_tiles(vec2 p)
{
    ivec2 tile = ivec2(floor(p));
    bool flip = random_domino_pair_tiling_flip(tile); // calculate domino direction
    ivec2 quadrant = ivec2(step(0.5, fract(p))) * 2 - 1; 

    // check alignment: can adjacent dominos be combined into a square?
    bool merge =
        flip ?
        random_domino_pair_tiling_flip(tile + ivec2(quadrant.x,0)) :
        !random_domino_pair_tiling_flip(tile + ivec2(0,quadrant.y));

    // display different parts of the pattern (merged, unmerged, all)
    if(fract(floor(iTime) / 3.0) < 0.5)
    {
        if(fract(floor(iTime) / 2.0) < 0.5)
        {
            if(!merge) return ivec2(0); // delete unmerged areas
        }
        else
        {
            if(merge) return ivec2(0); // delete merged areas
        }
    }

    return
        flip ?
        2*tile + ivec2(quadrant.x,0): // merge & quantize to horizontal dominos
        2*tile + ivec2(0,quadrant.y); // merge & quantize to vertical dominos
}

void mainImage(out vec4 o, in vec2 I)
{
    vec2 R = iResolution.xy;
    I = (2. * I - R) / R.y;
    vec3 color =
        all(lessThan(abs(I), vec2(1))) ?
        hash32(vec2(random_domino_tiling_merge_squares_across_tiles(5.0*I.xy))) :
        vec3(0);
    if(iMouse.z>0.0) // click to display domino outlines
    {
        // Thanks to Shane for domino outline visualization!
        I -= 4./R.y;
        vec3 color2 =
            all(lessThan(abs(I), vec2(1))) ?
            hash32(vec2(random_domino_tiling_merge_squares_across_tiles(5.0*I.xy))) :
            vec3(0); 
        if(any(greaterThan(abs(color2 - color), vec3(0)))) color *= 0.;
        //vec3 di = abs(color2 - color);
        //if(di.x>0. || di.y>0. || di.z>0.) color *= 0.;
    }
    //if(dFdx(color) != vec3(0.0) || dFdy(color) != vec3(0.0)) color *= 0.0; // failed attempt to draw outlines
    o = vec4(color, 1);
}
