void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv = (uv - 0.5) * 2.0;

    uv.x *= iResolution.x / iResolution.y;

    int arrayLength = 5;
    vec2[] voronoiPoints = vec2[] (vec2(-1, 0), vec2(0.5, 0), vec2(0, 0.5), vec2(0.5, 0.5), vec2(-0.5, 0));
    vec4[] voronoiColors = vec4[] (vec4(0, 1, 1, 1), vec4(1, 1, 0, 1), vec4(0, 0, 1, 1), vec4(0, 1, 0, 1), vec4(1, 0, 0, 1));

    float d = 0.0;
    float lengthToPoint = 0.0;
    float maxLength = 999.0;

    vec2 closestPoint = vec2(0, 0);

    vec4 pixelColor = vec4(0, 0, 0, 1);

    for(int i = 0; i < arrayLength; i++){
        lengthToPoint = distance(uv, voronoiPoints[i]);

        if(lengthToPoint < maxLength){
            maxLength = lengthToPoint;

            pixelColor = voronoiColors[i];
            closestPoint = voronoiPoints[i];
        }
    }

    d = distance(uv, closestPoint);

    //d = abs(d);

    d = sin(d * 100.);

    d = step(d, 0.75);

    float offD = 1. - d;

    vec3 color = (d * vec3(0.8, 0.42, 0.))  + (offD * vec3(0.64, 0.41, 0.16));
//
    // Output to screen
    fragColor = vec4(color, 1.0);
}
