void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float checkeredCount =10.0;
    vec2 checkeredLength = iResolution.xy / checkeredCount;
    vec3 filledColor = vec3(1.0);
    vec3 emptyColor = vec3(0);

    vec3 color = filledColor;

    vec2 intergerCoord = floor(fragCoord / checkeredLength);

    if(mod(intergerCoord.x + intergerCoord.y, 2.0) == 1.0){
        color = emptyColor;
    }
    fragColor = vec4(color,1.0);

}
