#define r(v) mat2(cos(v + vec4(0, 11, 33, 0)))

void mainImage(out vec4 o, vec2 u) {
    float d, s, i, j, f, t = iTime;

    // screen
    vec3 R = iResolution, q, 
         D = (vec3(u, R.y) - R/2.) / R.y;
         o *= i; 

    while(i++ < 44.) {

        // cam
        q.z  += -2.5;
        q.xz *= r(3.14/2. + cos(t * 2.) * 1.5), 
        q.zy *= r(t), 

        // sdf
        f = .1 / (
                    exp(
                        sin(
                            16. * atan(q.z, q.y) + t * 16.
                        ) * 4.
                    ) + 1.
                  ) + .06;

        s = max(
                max(length(q.yz) - 1. + f, abs(q.x) - .1),
                -max(length(q.yz) - .6, abs(q.x) - .2)
            );

        /* Displacement */
        j = 64.;
        while(j < 512.) 
            q *= j, 
            q.y > 0. 
                ? s += .08 / j 
                    * abs(dot(sin(q * 1.3), q/q)) 
                : s,
            j += j;

        // raymarch
        d += max(s = abs(s), 2e-3), 
        q = d * D,

        // material
        s < .02 
            ? o += (.01 - s) / d * i / 3.1 
            : o += vec4(0, 1, 2, 0) / 1e3;
    }
}
