void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord;

    uv -= vec2(0.5, 0.5) * iResolution.xy;

    vec3 col = vec3(1.0, 1.0, 1.0); 

    float r = 100.0;
    float r2 = 100.0;

    float t = iTime;


    vec2 centerAnime = vec2((r + r2) * sin(t), (r + r2) * cos(t));

    if (uv.x * uv.x + uv.y * uv.y < r * r)
    {

        float z = sqrt(r * r - uv.x * uv.x - uv.y * uv.y);
        vec3 normal = normalize(vec3(uv.x, uv.y, z));
        vec3 light = normalize(vec3(1.0, 1.0, 1.0));
        float diffuse = clamp(dot(normal, light), 0.0, 1.0);
        float I = diffuse * 0.7 + 0.3;
        vec3 d = vec3(0.0, 0.0, -1.0);
        vec3 reflection = reflect(d, normal);
        col = vec3(0.0, 1.0, 1.0) * I +
              vec3(1.0, 1.0, 1.0) * pow(clamp(dot(reflection, light), 0.0, 1.0), 10.0);
    }
    else if ((uv.x - centerAnime.x) * (uv.x - centerAnime.x) + (uv.y - centerAnime.y) * (uv.y - centerAnime.y) < r2 * r2)
    {

        float z = sqrt(r2 * r2 - (uv.x - centerAnime.x) * (uv.x - centerAnime.x) - (uv.y - centerAnime.y) * (uv.y - centerAnime.y));
        vec3 normal = normalize(vec3(uv.x - centerAnime.x, uv.y - centerAnime.y, z));
        vec3 light = normalize(vec3(1.0, 1.0, 1.0));
        float diffuse = clamp(dot(normal, light), 0.0, 1.0);
        float I = diffuse * 0.7 + 0.3;
        vec3 d = vec3(0.0, 0.0, -1.0);
        vec3 reflection = reflect(d, normal);
        col = vec3(1.0, 0.0, 0.0) * I +
          vec3(1.0, 1.0, 1.0) * pow(clamp(dot(reflection, light), 0.0, 1.0), 10.0);
    }

    fragColor = vec4(col, 1.0);
}
