// variant of https://shadertoy.com/view/mtfGDl

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( 2.*u - R ) / R.y;

    U.y = log2(1.-U.y) - iTime;
    U *= 10.;
    if ( mod(U.x+.5,2.) > 1. ) U.y += .5;
    vec2 F = fract( U +.5 ) -.5, V = F;
    V.y *= exp2(floor(U.y)/10.+iTime) / 1.4;  // why / 1.4 ? 

    V = smoothstep( 30./R.y, 0., abs(V)-.3 );                 // blocks
    F = smoothstep( 0.,1., (.5-abs(F)) / min(fwidth(F),.4) ); // lines

    O = vec4(min(F.x,F.y) *min(V.x,V.y) );                    // blacks + lines
    O = mix(O, vec4(.6), V.x*smoothstep(0.,.5,-dFdy(U.y)));   // fading to mid color
}
